frappe.listview_settings['Agency'] = {
    // Add colored status indicators for Active/Inactive agencies
    get_indicator: function(doc) {
        if (doc.is_active === 0) {
            return [__("Inactive"), "red", "is_active,=,0"];
        } else {
            return [__("Active"), "green", "is_active,=,1"];
        }
    },

    // Style the entire row based on active status
    add_fields: ["is_active"],


};
